mapper.insert:
insert into item3(
	item_id, item_name, price, description
)values(
	seq_item3.nextval, #{itemName}, #{price}, #{description}
)

insert into item3_attach(
	fullname, item_id, regdate
)values(
	#{fileName}, seq_item3.currval, sysdate
)

mapper.update:
